// SPDX-License-Identifier: AGPL-3.0-only

pragma solidity >=0.8.0;

/**
 * Slightly modified Solmate SafeTransferLib library for safe transfers of tokens
 * original ERC20 token has been replaced by Filecoin-safe ERC20. Updated token version performs
 * address normalization and allows to send tokens to f0/f1/f3/f4 addresses. In FVM no
 * tokens could be sent to the native actors.
 */

import {ERC20} from "./tokens/ERC20.sol";

/// @notice Safe ETH and ERC20 transfer library that gracefully handles missing return values.
/// @author Solmate (https://github.com/transmissions11/solmate/blob/main/src/utils/SafeTransferLib.sol)
/// @dev Use with caution! Some functions in this library knowingly create dirty bits at the destination of the free memory pointer.
/// @dev Note that none of the functions in this library check that a token has code at all! That responsibility is delegated to the caller.
library SafeTransferLib {
	/*//////////////////////////////////////////////////////////////
                             ETH OPERATIONS
    //////////////////////////////////////////////////////////////*/

	function safeTransferETH(address to, uint256 amount) internal {
		bool success;

		/// @solidity memory-safe-assembly
		assembly {
			// Transfer the ETH and store if it succeeded or not.
			success := call(gas(), to, amount, 0, 0, 0, 0)
		}

		require(success, "ETH_TRANSFER_FAILED");
	}

	/*//////////////////////////////////////////////////////////////
                            ERC20 OPERATIONS
    //////////////////////////////////////////////////////////////*/

	function safeTransferFrom(ERC20 token, address from, address to, uint256 amount) internal {
		bool success;

		/// @solidity memory-safe-assembly
		assembly {
			// Get a pointer to some free memory.
			let freeMemoryPointer := mload(0x40)

			// Write the abi-encoded calldata into memory, beginning with the function selector.
			mstore(freeMemoryPointer, 0x23b872dd00000000000000000000000000000000000000000000000000000000)
			mstore(add(freeMemoryPointer, 4), from) // Append the "from" argument.
			mstore(add(freeMemoryPointer, 36), to) // Append the "to" argument.
			mstore(add(freeMemoryPointer, 68), amount) // Append the "amount" argument.

			success := and(
				// Set success to whether the call reverted, if not we check it either
				// returned exactly 1 (can't just be non-zero data), or had no return data.
				or(and(eq(mload(0), 1), gt(returndatasize(), 31)), iszero(returndatasize())),
				// We use 100 because the length of our calldata totals up like so: 4 + 32 * 3.
				// We use 0 and 32 to copy up to 32 bytes of return data into the scratch space.
				// Counterintuitively, this call must be positioned second to the or() call in the
				// surrounding and() call or else returndatasize() will be zero during the computation.
				call(gas(), token, 0, freeMemoryPointer, 100, 0, 32)
			)
		}

		require(success, "TRANSFER_FROM_FAILED");
	}

	function safeTransfer(ERC20 token, address to, uint256 amount) internal {
		bool success;

		/// @solidity memory-safe-assembly
		assembly {
			// Get a pointer to some free memory.
			let freeMemoryPointer := mload(0x40)

			// Write the abi-encoded calldata into memory, beginning with the function selector.
			mstore(freeMemoryPointer, 0xa9059cbb00000000000000000000000000000000000000000000000000000000)
			mstore(add(freeMemoryPointer, 4), to) // Append the "to" argument.
			mstore(add(freeMemoryPointer, 36), amount) // Append the "amount" argument.

			success := and(
				// Set success to whether the call reverted, if not we check it either
				// returned exactly 1 (can't just be non-zero data), or had no return data.
				or(and(eq(mload(0), 1), gt(returndatasize(), 31)), iszero(returndatasize())),
				// We use 68 because the length of our calldata totals up like so: 4 + 32 * 2.
				// We use 0 and 32 to copy up to 32 bytes of return data into the scratch space.
				// Counterintuitively, this call must be positioned second to the or() call in the
				// surrounding and() call or else returndatasize() will be zero during the computation.
				call(gas(), token, 0, freeMemoryPointer, 68, 0, 32)
			)
		}

		require(success, "TRANSFER_FAILED");
	}

	function safeApprove(ERC20 token, address to, uint256 amount) internal {
		bool success;

		/// @solidity memory-safe-assembly
		assembly {
			// Get a pointer to some free memory.
			let freeMemoryPointer := mload(0x40)

			// Write the abi-encoded calldata into memory, beginning with the function selector.
			mstore(freeMemoryPointer, 0x095ea7b300000000000000000000000000000000000000000000000000000000)
			mstore(add(freeMemoryPointer, 4), to) // Append the "to" argument.
			mstore(add(freeMemoryPointer, 36), amount) // Append the "amount" argument.

			success := and(
				// Set success to whether the call reverted, if not we check it either
				// returned exactly 1 (can't just be non-zero data), or had no return data.
				or(and(eq(mload(0), 1), gt(returndatasize(), 31)), iszero(returndatasize())),
				// We use 68 because the length of our calldata totals up like so: 4 + 32 * 2.
				// We use 0 and 32 to copy up to 32 bytes of return data into the scratch space.
				// Counterintuitively, this call must be positioned second to the or() call in the
				// surrounding and() call or else returndatasize() will be zero during the computation.
				call(gas(), token, 0, freeMemoryPointer, 68, 0, 32)
			)
		}

		require(success, "APPROVE_FAILED");
	}
}

contracts/libraries/tokens/ERC20.sol.content:
// SPDX-License-Identifier: AGPL-3.0-only
pragma solidity >=0.8.0;

/**
 * Slightly modified Solmate ERC20 token implementation with Filecoin-safe address
 * conversions that allow this token to be transferred to f0/f1/f3/f4 addresses.
 */

import {FilAddress} from "lib/fevmate/contracts/utils/FilAddress.sol";

/// @notice Modern and gas efficient ERC20 + EIP-2612 implementation.
/// @author Solmate (https://github.com/transmissions11/solmate/blob/main/src/tokens/ERC20.sol)
/// @author Modified from Uniswap (https://github.com/Uniswap/uniswap-v2-core/blob/master/contracts/UniswapV2ERC20.sol)
/// @dev Do not manually set balances without updating totalSupply, as the sum of all user balances must not exceed it.
abstract contract ERC20 {
	using FilAddress for *;

	/*//////////////////////////////////////////////////////////////
                                 EVENTS
    //////////////////////////////////////////////////////////////*/

	event Transfer(address indexed from, address indexed to, uint256 amount);

	event Approval(address indexed owner, address indexed spender, uint256 amount);

	/*//////////////////////////////////////////////////////////////
                            METADATA STORAGE
    //////////////////////////////////////////////////////////////*/

	string public name;

	string public symbol;

	uint8 public immutable decimals;

	/*//////////////////////////////////////////////////////////////
                              ERC20 STORAGE
    //////////////////////////////////////////////////////////////*/

	uint256 public totalSupply;

	mapping(address => uint256) balances;

	mapping(address => mapping(address => uint256)) allowances;

	/*//////////////////////////////////////////////////////////////
                            EIP-2612 STORAGE
    //////////////////////////////////////////////////////////////*/

	uint256 internal immutable INITIAL_CHAIN_ID;

	bytes32 internal immutable INITIAL_DOMAIN_SEPARATOR;

	mapping(address => uint256) public nonces;

	/*//////////////////////////////////////////////////////////////
                               CONSTRUCTOR
    //////////////////////////////////////////////////////////////*/

	constructor(string memory _name, string memory _symbol, uint8 _decimals) {
		name = _name;
		symbol = _symbol;
		decimals = _decimals;

		INITIAL_CHAIN_ID = block.chainid;
		INITIAL_DOMAIN_SEPARATOR = computeDomainSeparator();
	}

	/*//////////////////////////////////////////////////////////////
                               ERC20 LOGIC
    //////////////////////////////////////////////////////////////*/

	function approve(address spender, uint256 amount) public virtual returns (bool) {
		spender = spender.normalize();
		allowances[msg.sender][spender] = amount;

		emit Approval(msg.sender, spender, amount);

		return true;
	}

	function transfer(address to, uint256 amount) public virtual returns (bool) {
		to = to.normalize();

		balances[msg.sender] -= amount;

		// Cannot overflow because the sum of all user
		// balances can't exceed the max uint256 value.
		unchecked {
			balances[to] += amount;
		}

		emit Transfer(msg.sender, to, amount);

		return true;
	}

	function transferFrom(address from, address to, uint256 amount) public virtual returns (bool) {
		from = from.normalize();
		to = to.normalize();

		uint256 allowed = allowances[from][msg.sender]; // Saves gas for limited approvals.

		if (allowed != type(uint256).max) allowances[from][msg.sender] = allowed - amount;

		balances[from] -= amount;

		// Cannot overflow because the sum of all user
		// balances can't exceed the max uint256 value.
		unchecked {
			balances[to] += amount;
		}

		emit Transfer(from, to, amount);

		return true;
	}

	/*//////////////////////////////////////
                 ERC-20 GETTERS
    //////////////////////////////////////*/

	function balanceOf(address a) public view virtual returns (uint) {
		return balances[a.normalize()];
	}

	function allowance(address owner, address spender) public view virtual returns (uint) {
		return allowances[owner.normalize()][spender.normalize()];
	}

	/*//////////////////////////////////////////////////////////////
                             EIP-2612 LOGIC
    //////////////////////////////////////////////////////////////*/

	function permit(
		address owner,
		address spender,
		uint256 value,
		uint256 deadline,
		uint8 v,
		bytes32 r,
		bytes32 s
	) public virtual {
		// permits only supported by f4 addresses no need to check the owner
		spender = spender.normalize();

		require(deadline >= block.timestamp, "PERMIT_DEADLINE_EXPIRED");

		// Unchecked because the only math done is incrementing
		// the owner's nonce which cannot realistically overflow.
		unchecked {
			address recoveredAddress = ecrecover(
				keccak256(
					abi.encodePacked(
						"\x19\x01",
						DOMAIN_SEPARATOR(),
						keccak256(
							abi.encode(
								keccak256(
									"Permit(address owner,address spender,uint256 value,uint256 nonce,uint256 deadline)"
								),
								owner,
								spender,
								value,
								nonces[owner]++,
								deadline
							)
						)
					)
				),
				v,
				r,
				s
			);

			require(recoveredAddress != address(0) && recoveredAddress == owner, "INVALID_SIGNER");

			allowances[recoveredAddress][spender] = value;
		}

		emit Approval(owner, spender, value);
	}

	function DOMAIN_SEPARATOR() public view virtual returns (bytes32) {
		return block.chainid == INITIAL_CHAIN_ID ? INITIAL_DOMAIN_SEPARATOR : computeDomainSeparator();
	}

	function computeDomainSeparator() internal view virtual returns (bytes32) {
		return
			keccak256(
				abi.encode(
					keccak256("EIP712Domain(string name,string version,uint256 chainId,address verifyingContract)"),
					keccak256(bytes(name)),
					keccak256("1"),
					block.chainid,
					address(this)
				)
			);
	}

	/*//////////////////////////////////////////////////////////////
                        INTERNAL MINT/BURN LOGIC
    //////////////////////////////////////////////////////////////*/

	function _mint(address to, uint256 amount) internal virtual {
		to = to.normalize();
		totalSupply += amount;

		// Cannot overflow because the sum of all user
		// balances can't exceed the max uint256 value.
		unchecked {
			balances[to] += amount;
		}

		emit Transfer(address(0), to, amount);
	}

	function _burn(address from, uint256 amount) internal virtual {
		from = from.normalize();
		balances[from] -= amount;

		// Cannot underflow because a user's balance
		// will never be larger than the total supply.
		unchecked {
			totalSupply -= amount;
		}

		emit Transfer(from, address(0), amount);
	}
}

contracts/libraries/tokens/ERC4626.sol.content:
// SPDX-License-Identifier: AGPL-3.0-only
pragma solidity >=0.8.0;

/**
 * Slightly modified Solmate ERC4626 token implementation the original ERC20 token
 * has been replaced by Filecoin-safe ERC20.
 */

import {ERC20} from "./ERC20.sol";
import {SafeTransferLib} from "../SafeTransferLib.sol";
import {FixedPointMathLib} from "lib/solmate/src/utils/FixedPointMathLib.sol";
import {FilAddress} from "lib/fevmate/contracts/utils/FilAddress.sol";

/// @notice Minimal ERC4626 tokenized Vault implementation.
/// @author Solmate (https://github.com/transmissions11/solmate/blob/main/src/mixins/ERC4626.sol)
abstract contract ERC4626 is ERC20 {
	using SafeTransferLib for ERC20;
	using FixedPointMathLib for uint256;

	/*//////////////////////////////////////////////////////////////
                                 EVENTS
    //////////////////////////////////////////////////////////////*/

	event Deposit(address indexed caller, address indexed owner, uint256 assets, uint256 shares);

	event Withdraw(
		address indexed caller,
		address indexed receiver,
		address indexed owner,
		uint256 assets,
		uint256 shares
	);

	/*//////////////////////////////////////////////////////////////
                               IMMUTABLES
    //////////////////////////////////////////////////////////////*/

	ERC20 public immutable asset;

	constructor(ERC20 _asset, string memory _name, string memory _symbol) ERC20(_name, _symbol, _asset.decimals()) {
		asset = _asset;
	}

	/*//////////////////////////////////////////////////////////////
                        DEPOSIT/WITHDRAWAL LOGIC
    //////////////////////////////////////////////////////////////*/

	function deposit(uint256 assets, address receiver) public virtual returns (uint256 shares) {
		// Check for rounding error since we round down in previewDeposit.
		require((shares = previewDeposit(assets)) != 0, "ZERO_SHARES");

		// Need to transfer before minting or ERC777s could reenter.
		asset.safeTransferFrom(msg.sender, address(this), assets);

		_mint(receiver, shares);

		emit Deposit(msg.sender, receiver, assets, shares);

		afterDeposit(assets, shares);
	}

	function mint(uint256 shares, address receiver) public virtual returns (uint256 assets) {
		assets = previewMint(shares); // No need to check for rounding error, previewMint rounds up.

		// Need to transfer before minting or ERC777s could reenter.
		asset.safeTransferFrom(msg.sender, address(this), assets);

		_mint(receiver, shares);

		emit Deposit(msg.sender, receiver, assets, shares);

		afterDeposit(assets, shares);
	}

	function withdraw(uint256 assets, address receiver, address owner) public virtual returns (uint256 shares) {
		shares = previewWithdraw(assets); // No need to check for rounding error, previewWithdraw rounds up.

		if (msg.sender != owner) {
			uint256 allowed = allowances[owner][msg.sender]; // Saves gas for limited approvals.

			if (allowed != type(uint256).max) allowances[owner][msg.sender] = allowed - shares;
		}

		beforeWithdraw(assets, shares);

		_burn(owner, shares);

		emit Withdraw(msg.sender, receiver, owner, assets, shares);

		asset.safeTransfer(receiver, assets);
	}

	function redeem(uint256 shares, address receiver, address owner) public virtual returns (uint256 assets) {
		if (msg.sender != owner) {
			uint256 allowed = allowances[owner][msg.sender]; // Saves gas for limited approvals.

			if (allowed != type(uint256).max) allowances[owner][msg.sender] = allowed - shares;
		}

		// Check for rounding error since we round down in previewRedeem.
		require((assets = previewRedeem(shares)) != 0, "ZERO_ASSETS");

		beforeWithdraw(assets, shares);

		_burn(owner, shares);

		emit Withdraw(msg.sender, receiver, owner, assets, shares);

		asset.safeTransfer(receiver, assets);
	}

	/*//////////////////////////////////////////////////////////////
                            ACCOUNTING LOGIC
    //////////////////////////////////////////////////////////////*/

	function totalAssets() public view virtual returns (uint256);

	function convertToShares(uint256 assets) public view virtual returns (uint256) {
		uint256 supply = totalSupply; // Saves an extra SLOAD if totalSupply is non-zero.

		return supply == 0 ? assets : assets.mulDivDown(supply, totalAssets());
	}

	function convertToAssets(uint256 shares) public view virtual returns (uint256) {
		uint256 supply = totalSupply; // Saves an extra SLOAD if totalSupply is non-zero.

		return supply == 0 ? shares : shares.mulDivDown(totalAssets(), supply);
	}

	function previewDeposit(uint256 assets) public view virtual returns (uint256) {
		return convertToShares(assets);
	}

	function previewMint(uint256 shares) public view virtual returns (uint256) {
		uint256 supply = totalSupply; // Saves an extra SLOAD if totalSupply is non-zero.

		return supply == 0 ? shares : shares.mulDivUp(totalAssets(), supply);
	}

	function previewWithdraw(uint256 assets) public view virtual returns (uint256) {
		uint256 supply = totalSupply; // Saves an extra SLOAD if totalSupply is non-zero.

		return supply == 0 ? assets : assets.mulDivUp(supply, totalAssets());
	}

	function previewRedeem(uint256 shares) public view virtual returns (uint256) {
		return convertToAssets(shares);
	}

	/*//////////////////////////////////////////////////////////////
                     DEPOSIT/WITHDRAWAL LIMIT LOGIC
    //////////////////////////////////////////////////////////////*/

	function maxDeposit(address) public view virtual returns (uint256) {
		return type(uint256).max;
	}

	function maxMint(address) public view virtual returns (uint256) {
		return type(uint256).max;
	}

	function maxWithdraw(address owner) public view virtual returns (uint256) {
		return convertToAssets(balances[owner]);
	}

	function maxRedeem(address owner) public view virtual returns (uint256) {
		return balances[owner];
	}

	/*//////////////////////////////////////////////////////////////
                          INTERNAL HOOKS LOGIC
    //////////////////////////////////////////////////////////////*/

	function beforeWithdraw(uint256 assets, uint256 shares) internal virtual {}

	function afterDeposit(uint256 assets, uint256 shares) internal virtual {}
}

