// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import {ClFILToken} from "./ClFIL.sol";
import {ReentrancyGuard} from "lib/solmate/src/utils/ReentrancyGuard.sol";
import {AccessControl} from "lib/openzeppelin-contracts/contracts//access/AccessControl.sol";
import {SafeTransferLib} from "./libraries/SafeTransferLib.sol";
import {MinerAPI, CommonTypes, MinerTypes} from "lib/filecoin-solidity/contracts/v0.8/MinerAPI.sol";
import {FilAddresses} from "lib/filecoin-solidity/contracts/v0.8/utils/FilAddresses.sol";
import {FilAddress} from "lib/fevmate/contracts/utils/FilAddress.sol";
import {SendAPI} from "lib/filecoin-solidity/contracts/v0.8/SendAPI.sol";

import {ILiquidStaking} from "./interfaces/ILiquidStaking.sol";
import {IStorageProviderCollateralClient} from "./interfaces/IStorageProviderCollateralClient.sol";
import {IStorageProviderRegistryClient} from "./interfaces/IStorageProviderRegistryClient.sol";
import {IBigInts} from "./libraries/BigInts.sol";

/**
 * @title LiquidStaking contract allows users to stake/unstake FIL to earn
 * Filecoin mining rewards. Staked FIL is allocated to Storage Providers (SPs) that
 * perform filecoin storage mining operations. This contract acts as a beneficiary address
 * for each SP that uses FIL capital for pledges.
 *
 * While staking FIL user would get clFIL token in exchange, the token follows ERC4626
 * standard and it's price is recalculated once mining rewards are distributed to the
 * liquid staking pool and once new FIL is deposited. Please note that LiquidStaking contract
 * performs wrapping of the native FIL into Wrapped Filecoin (WFIL) token.
 */
contract LiquidStaking is ILiquidStaking, ClFILToken, ReentrancyGuard, AccessControl {
	using SafeTransferLib for *;
	using FilAddress for address;

	/// @notice The current total amount of FIL that is allocated to SPs.
	uint256 public totalFilPledged;
	uint256 private constant BASIS_POINTS = 10000;
	uint256 public adminFee;
	uint256 public baseProfitShare;
	address public rewardCollector;

	IStorageProviderCollateralClient internal collateral;
	IStorageProviderRegistryClient internal registry;
	IBigInts internal immutable BigInts;

	bytes32 private constant LIQUID_STAKING_ADMIN = keccak256("LIQUID_STAKING_ADMIN");
	bytes32 private constant FEE_DISTRIBUTOR = keccak256("FEE_DISTRIBUTOR");
	bytes32 private constant SLASHING_AGENT = keccak256("SLASHING_AGENT");

	mapping(uint64 => uint256) public profitShares;
	mapping(uint64 => bool) public activeSlashings;

	constructor(
		address _wFIL,
		uint256 _adminFee,
		uint256 _baseProfitShare,
		address _rewardCollector,
		address _bigIntsLib
	) ClFILToken(_wFIL) {
		require(_adminFee <= 10000, "INVALID_ADMIN_FEE");
		require(_rewardCollector != address(0), "INVALID_REWARD_COLLECTOR");
		adminFee = _adminFee;
		baseProfitShare = _baseProfitShare;
		rewardCollector = _rewardCollector;

		BigInts = IBigInts(_bigIntsLib);

		_setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
		grantRole(LIQUID_STAKING_ADMIN, msg.sender);
		grantRole(FEE_DISTRIBUTOR, msg.sender);
		grantRole(SLASHING_AGENT, msg.sender);
	}

	receive() external payable virtual {}

	fallback() external payable virtual {}

	/**
	 * @notice Stake FIL to the Liquid Staking pool and get clFIL in return
	 * native FIL is wrapped into WFIL and deposited into LiquidStaking
	 *
	 * @notice msg.value is the amount of FIL to stake
	 */
	function stake() external payable nonReentrant returns (uint256 shares) {
		uint256 assets = msg.value;

		// Check for rounding error since we round down in previewDeposit.
		require((shares = previewDeposit(assets)) != 0, "ZERO_SHARES");

		_wrapWETH9(address(this));

		_mint(msg.sender, shares);

		emit Deposit(msg.sender, msg.sender, assets, shares);

		afterDeposit(assets, shares);
	}

	/**
	 * @notice Unstake FIL from the Liquid Staking pool and burn clFIL tokens
	 * @param shares Total clFIL amount to burn (unstake)
	 * @param owner Original owner of clFIL tokens
	 * @dev Please note that unstake amount has to be clFIL shares (not FIL assets)
	 */
	function unstake(uint256 shares, address owner) external nonReentrant returns (uint256 assets) {
		if (msg.sender != owner) {
			uint256 allowed = allowances[owner][msg.sender]; // Saves gas for limited approvals.

			if (allowed != type(uint256).max) allowances[owner][msg.sender] = allowed - shares;
		}

		// Check for rounding error since we round down in previewRedeem.
		require((assets = previewRedeem(shares)) != 0, "ZERO_ASSETS");

		beforeWithdraw(assets, shares);

		_burn(owner, shares);

		emit Unstaked(msg.sender, owner, assets, shares);

		_unwrapWFIL(msg.sender, assets);
	}

	/**
	 * @notice Unstake FIL from the Liquid Staking pool and burn clFIL tokens
	 * @param assets Total FIL amount to unstake
	 * @param owner Original owner of clFIL tokens
	 */
	function unstakeAssets(uint256 assets, address owner) external nonReentrant returns (uint256 shares) {
		shares = previewWithdraw(assets); // No need to check for rounding error, previewWithdraw rounds up.

		if (msg.sender != owner) {
			uint256 allowed = allowances[owner][msg.sender]; // Saves gas for limited approvals.

			if (allowed != type(uint256).max) allowances[owner][msg.sender] = allowed - shares;
		}

		beforeWithdraw(assets, shares);

		_burn(owner, shares);

		emit Unstaked(msg.sender, owner, assets, shares);

		_unwrapWFIL(msg.sender, assets);
	}

	/**
	 * @notice Pledge FIL assets from liquid staking pool to miner pledge for one sector
	 * @param amount Amount of FIL to pledge from Liquid Staking Pool
	 */
	function pledge(uint256 amount) external virtual nonReentrant {
		require(amount <= totalAssets(), "PLEDGE_WITHDRAWAL_OVERFLOW");

		address ownerAddr = msg.sender.normalize();
		(bool isID, uint64 ownerId) = ownerAddr.getActorID();
		require(isID, "INACTIVE_ACTOR_ID");
		require(!activeSlashings[ownerId], "ACTIVE_SLASHING");

		collateral.lock(ownerId, amount);

		(, , uint64 minerId, ) = registry.getStorageProvider(ownerId);
		CommonTypes.FilActorId minerActorId = CommonTypes.FilActorId.wrap(minerId);

		emit Pledge(ownerId, minerId, amount);

		WFIL.withdraw(amount);

		totalFilPledged += amount;

		SendAPI.send(minerActorId, amount); // send FIL to the miner actor
	}

	/**
	 * @notice Withdraw initial pledge from Storage Provider's Miner Actor by `ownerId`
	 * This function is triggered when sector is not extended by miner actor and initial pledge unlocked
	 * @param ownerId Storage provider owner ID
	 * @param amount Initial pledge amount
	 * @dev Please note that pledge amount withdrawn couldn't exceed used allocation by SP
	 */
	function withdrawPledge(uint64 ownerId, uint256 amount) external virtual nonReentrant {
		require(hasRole(FEE_DISTRIBUTOR, msg.sender), "INVALID_ACCESS");
		(, , uint64 minerId, ) = registry.getStorageProvider(ownerId);
		CommonTypes.FilActorId minerActorId = CommonTypes.FilActorId.wrap(minerId);

		CommonTypes.BigInt memory withdrawnBInt = MinerAPI.withdrawBalance(minerActorId, BigInts.fromUint256(amount));

		(uint256 withdrawn, bool abort) = BigInts.toUint256(withdrawnBInt);
		require(!abort, "INCORRECT_BIG_NUM");
		require(withdrawn == amount, "INCORRECT_WITHDRAWAL_AMOUNT");

		WFIL.deposit{value: withdrawn}();

		registry.increasePledgeRepayment(ownerId, amount);

		totalFilPledged -= amount;

		collateral.fit(ownerId);

		emit PledgeRepayment(ownerId, minerId, amount);
	}

	/**
	 * @notice Report slashing of SP accured on the Filecoin network
	 * This function is triggered when SP get continiously slashed by faulting it's sectors
	 * @param _ownerId Storage provider owner ID
	 * @param _slashingAmt Slashing amount
	 *
	 * @dev Please note that slashing amount couldn't exceed the total amount of collateral provided by SP.
	 * If sector has been slashed for 42 days and automatically terminated both operations
	 * would take place after one another: slashing report and initial pledge withdrawal
	 * which is the remaining pledge for a terminated sector.
	 */
	function reportSlashing(uint64 _ownerId, uint256 _slashingAmt) external virtual nonReentrant {
		require(hasRole(SLASHING_AGENT, msg.sender), "INVALID_ACCESS");
		(, , uint64 minerId, ) = registry.getStorageProvider(_ownerId);

		collateral.slash(_ownerId, _slashingAmt);

		activeSlashings[_ownerId] = true;

		emit ReportSlashing(_ownerId, minerId, _slashingAmt);
	}

	/**
	 * @notice Report recovery of previously slashed sectors for SP with `_ownerId`
	 * @param _ownerId Storage provider owner ID
	 */
	function reportRecovery(uint64 _ownerId) external virtual {
		require(hasRole(SLASHING_AGENT, msg.sender), "INVALID_ACCESS");
		require(activeSlashings[_ownerId], "NO_ACTIVE_SLASHINGS");
		(, , uint64 minerId, ) = registry.getStorageProvider(_ownerId);

		activeSlashings[_ownerId] = false;

		emit ReportRecovery(_ownerId, minerId);
	}

	struct WithdrawRewardsLocalVars {
		uint256 restakingRatio;
		address restakingAddress;
		uint256 withdrawn;
		bool abort;
		bool isRestaking;
		uint256 protocolFees;
		uint256 stakingProfit;
		uint256 restakingAmt;
		uint256 protocolShare;
		uint256 spShare;
		CommonTypes.FilActorId minerActorId;
		CommonTypes.BigInt withdrawnBInt;
	}

	/**
	 * @notice Withdraw FIL assets from Storage Provider by `ownerId` and it's Miner actor
	 * and restake `restakeAmount` into the Storage Provider specified f4 address
	 * @param ownerId Storage provider owner ID
	 * @param amount Withdrawal amount
	 */
	function withdrawRewards(uint64 ownerId, uint256 amount) external virtual nonReentrant {
		require(hasRole(FEE_DISTRIBUTOR, msg.sender), "INVALID_ACCESS");
		WithdrawRewardsLocalVars memory vars;

		(, , uint64 minerId, ) = registry.getStorageProvider(ownerId);
		vars.minerActorId = CommonTypes.FilActorId.wrap(minerId);

		vars.withdrawnBInt = MinerAPI.withdrawBalance(vars.minerActorId, BigInts.fromUint256(amount));

		(vars.withdrawn, vars.abort) = BigInts.toUint256(vars.withdrawnBInt);
		require(!vars.abort, "INCORRECT_BIG_NUM");
		require(vars.withdrawn == amount, "INCORRECT_WITHDRAWAL_AMOUNT");

		vars.stakingProfit = (vars.withdrawn * profitShares[ownerId]) / BASIS_POINTS;
		vars.protocolFees = (vars.withdrawn * adminFee) / BASIS_POINTS;
		vars.protocolShare = vars.stakingProfit + vars.protocolFees;

		(vars.restakingRatio, vars.restakingAddress) = registry.restakings(ownerId);

		vars.isRestaking = vars.restakingRatio > 0 && vars.restakingAddress != address(0);

		if (vars.isRestaking) {
			vars.restakingAmt = ((vars.withdrawn - vars.protocolShare) * vars.restakingRatio) / BASIS_POINTS;
		}

		vars.spShare = vars.withdrawn - (vars.protocolShare + vars.restakingAmt);

		WFIL.deposit{value: vars.protocolShare}();
		WFIL.transfer(rewardCollector, vars.protocolFees);

		SendAPI.send(CommonTypes.FilActorId.wrap(ownerId), vars.spShare);

		registry.increaseRewards(ownerId, vars.stakingProfit);
		collateral.fit(ownerId);

		if (vars.isRestaking) {
			_restake(vars.restakingAmt, vars.restakingAddress);
		}
	}

	/**
	 * @dev Updates profit sharing requirements for SP with `_ownerId` by `_profitShare` percentage
	 * @notice Only triggered by Liquid Staking admin or registry contract while registering SP
	 * @param _ownerId Storage provider owner ID
	 * @param _profitShare Percentage of profit sharing
	 */
	function updateProfitShare(uint64 _ownerId, uint256 _profitShare) external {
		require(hasRole(LIQUID_STAKING_ADMIN, msg.sender) || msg.sender == address(registry), "INVALID_ACCESS");

		if (_profitShare == 0) {
			profitShares[_ownerId] = baseProfitShare;

			emit ProfitShareUpdate(_ownerId, 0, baseProfitShare);
		} else {
			uint256 prevShare = profitShares[_ownerId];
			require(_profitShare <= 8000, "PROFIT_SHARE_OVERFLOW");
			require(_profitShare != prevShare, "SAME_PROFIT_SHARE");

			profitShares[_ownerId] = _profitShare;

			emit ProfitShareUpdate(_ownerId, prevShare, _profitShare);
		}
	}

	/**
	 * @notice Restakes `assets` for a specified `target` address
	 * @param assets Amount of assets to restake
	 * @param target f4 address to receive clFIL tokens
	 */
	function _restake(uint256 assets, address target) internal returns (uint256 shares) {
		require((shares = previewDeposit(assets)) != 0, "ZERO_SHARES");

		_mint(target, shares);

		emit Deposit(target, target, assets, shares);

		afterDeposit(assets, shares);
	}

	/**
	 * @notice Returns total amount of assets backing clFIL, that includes
	 * buffered capital in the pool and pledged capital to the SPs.
	 */
	function totalAssets() public view virtual override returns (uint256) {
		return totalFilAvailable() + totalFilPledged;
	}

	/**
	 * @notice Returns total amount of fees held by LSP for a specific SP with `_ownerId`
	 * @param _ownerId Storage Provider owner ID
	 */
	function totalFees(uint64 _ownerId) external view virtual override returns (uint256) {
		return profitShares[_ownerId] + adminFee;
	}

	/**
	 * @notice Returns pool usage ratio to determine what percentage of FIL
	 * is pledged compared to the total amount of FIL staked.
	 */
	function getUsageRatio() public view virtual returns (uint256) {
		return (totalFilPledged * BASIS_POINTS) / (totalFilAvailable() + totalFilPledged);
	}

	/**
	 * @notice Updates StorageProviderCollateral contract address
	 * @param newAddr StorageProviderCollateral contract address
	 */
	function setCollateralAddress(address newAddr) public {
		require(hasRole(LIQUID_STAKING_ADMIN, msg.sender), "INVALID_ACCESS");
		require(newAddr != address(0), "INVALID_ADDRESS");

		address prevCollateral = address(collateral);
		require(prevCollateral != newAddr, "SAME_ADDRESS");

		collateral = IStorageProviderCollateralClient(newAddr);

		emit SetCollateralAddress(newAddr);
	}

	/**
	 * @notice Returns the amount of WFIL available on the liquid staking contract
	 */
	function totalFilAvailable() public view returns (uint256) {
		return asset.balanceOf(address(this));
	}

	/**
	 * @notice Updates StorageProviderRegistry contract address
	 * @param newAddr StorageProviderRegistry contract address
	 */
	function setRegistryAddress(address newAddr) public {
		require(hasRole(LIQUID_STAKING_ADMIN, msg.sender), "INVALID_ACCESS");
		require(newAddr != address(0), "INVALID_ADDRESS");

		address prevRegistry = address(registry);
		require(prevRegistry != newAddr, "SAME_ADDRESS");

		registry = IStorageProviderRegistryClient(newAddr);

		emit SetRegistryAddress(newAddr);
	}

	/**
	 * @notice Updates admin fee for the protocol revenue
	 * @param fee New admin fee
	 * @dev Make sure that admin fee is not greater than 20%
	 */
	function updateAdminFee(uint256 fee) public {
		require(hasRole(LIQUID_STAKING_ADMIN, msg.sender), "INVALID_ACCESS");
		require(fee <= 2000, "ADMIN_FEE_OVERFLOW");

		uint256 prevFee = adminFee;
		require(fee != prevFee, "SAME_ADMIN_FEE");

		adminFee = fee;

		emit UpdateAdminFee(fee);
	}

	/**
	 * @notice Updates base profit sharing ratio
	 * @param share New base profit sharing ratio
	 * @dev Make sure that profit sharing is not greater than 80%
	 */
	function updateBaseProfitShare(uint256 share) public {
		require(hasRole(LIQUID_STAKING_ADMIN, msg.sender), "INVALID_ACCESS");
		require(share <= 8000 && share > 0, "PROFIT_SHARE_OVERFLOW");

		uint256 prevShare = baseProfitShare;
		require(share != prevShare, "SAME_PROFIT_SHARE");

		baseProfitShare = share;

		emit UpdateBaseProfitShare(share);
	}

	/**
	 * @notice Updates reward collector address of the protocol revenue
	 * @param collector New rewards collector address
	 */
	function updateRewardsCollector(address collector) public {
		require(hasRole(LIQUID_STAKING_ADMIN, msg.sender), "INVALID_ACCESS");
		require(collector != address(0), "INVALID_ADDRESS");

		address prevAddr = rewardCollector;
		require(collector != prevAddr, "SAME_COLLECTOR_ADDRESS");

		rewardCollector = collector;

		emit UpdateRewardCollector(collector);
	}

	/**
	 * @notice Triggers changeBeneficiary Miner actor call
	 * @param minerId Miner actor ID
	 * @param targetPool LSP smart contract address
	 * @param quota Total beneficiary quota
	 * @param expiration Expiration epoch
	 */
	function forwardChangeBeneficiary(
		uint64 minerId,
		address targetPool,
		uint256 quota,
		int64 expiration
	) external virtual {
		require(msg.sender == address(registry), "INVALID_ACCESS");
		require(targetPool == address(this), "INCORRECT_ADDRESS");

		CommonTypes.FilActorId filMinerId = CommonTypes.FilActorId.wrap(minerId);

		MinerTypes.ChangeBeneficiaryParams memory params;

		params.new_beneficiary = FilAddresses.fromEthAddress(targetPool);
		params.new_quota = BigInts.fromUint256(quota);
		params.new_expiration = CommonTypes.ChainEpoch.wrap(expiration);

		MinerAPI.changeBeneficiary(filMinerId, params);
	}

	/**
	 * @notice Wraps FIL into WFIL and transfers it to the `_recipient` address
	 * @param _recipient WFIL recipient address
	 */
	function _wrapWETH9(address _recipient) internal {
		uint256 amount = msg.value;
		WFIL.deposit{value: amount}();
		WFIL.transfer(_recipient, amount);
	}

	/**
	 * @notice Unwraps `_amount` of WFIL into FIL and transfers it to the `_recipient` address
	 * @param _recipient WFIL recipient address
	 */
	function _unwrapWFIL(address _recipient, uint256 _amount) internal {
		uint256 balanceWETH9 = WFIL.balanceOf(address(this));
		require(balanceWETH9 >= _amount, "Insufficient WETH9");

		if (balanceWETH9 > 0) {
			WFIL.withdraw(_amount);
			_recipient.safeTransferETH(_amount);
		}
	}
}

contracts/oracle/PledgeOracle.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import "../interfaces/IPledgeOracle.sol";
import {AccessControl} from "lib/openzeppelin-contracts/contracts//access/AccessControl.sol";

/**
 * @title Pledge Oracle allows to record PreCommitDeposit and InitialPledge
 * historical amounts. This oracle contract is used to determine the amount of FIL
 * required to send to Storage Provider's miner actors while staking system
 * pledges FIL. It accounts for both PreCommitDeposit and InitialPledge amounts.
 *
 * @notice Oracle is expected to be reported for each epoch. However, there are no
 * such big differences in those fees being in a short period of time. In the future iteration,
 * a time-weighted average fee amount could be used with some level of deviation.
 *
 * @notice For the time being only selected actors could report into the Oracle to minimize
 * the risk of incorrect reports being recorded.
 */
contract PledgeOracle is IPledgeOracle, AccessControl {
	struct Record {
		uint256 preCommitDeposit;
		uint256 initialPledge;
	}

	uint256 public lastEpochReport;
	uint256 private constant epochTime = 30 seconds;
	uint256 public immutable genesisEpoch;

	bytes32 private constant ORACLE_REPORTER = keccak256("ORACLE_REPORTER");
	bytes32 private constant ORACLE_ADMIN = keccak256("ORACLE_ADMIN");

	// Mapping of fee records for network epochs
	mapping(uint256 => Record) records;

	constructor(uint256 genesis) {
		genesisEpoch = genesis;
		lastEpochReport = genesis;
		_setupRole(DEFAULT_ADMIN_ROLE, msg.sender);

		_setRoleAdmin(ORACLE_REPORTER, ORACLE_ADMIN);
		grantRole(ORACLE_ADMIN, msg.sender);
		grantRole(ORACLE_REPORTER, msg.sender);
	}

	/**
	 * @notice Updates record with `epoch` and fees
	 * @param epoch Filecoin network Epoch number
	 * @param preCommitDeposit PreCommitDeposit fee
	 * @param initialPledge Initial pledge fee
	 *
	 * @notice Only triggered by address with `ORACLE_REPORTER` role
	 */
	function updateRecord(uint256 epoch, uint256 preCommitDeposit, uint256 initialPledge) external {
		require(hasRole(ORACLE_REPORTER, msg.sender), "INVALID_ACCESS");
		require(epoch >= lastEpochReport, "PREVIOUS_EPOCH_RECORD_ATTEMPT");
		require(preCommitDeposit > 0 && initialPledge > 0, "INVALID_FEES");

		Record memory record;
		record.preCommitDeposit = preCommitDeposit;
		record.initialPledge = initialPledge;

		records[epoch] = record;
		lastEpochReport = epoch;

		emit RecordUpdated(epoch, preCommitDeposit, initialPledge);
	}

	/**
	 * @notice Return total amount of fees for PreCommitDeposit and Initial Pledge
	 */
	function getPledgeFees() external view returns (uint256) {
		Record memory record = records[lastEpochReport];
		return (record.preCommitDeposit + record.initialPledge);
	}

	/**
	 * @notice Returns record information with PreCommitDeposit and Initial Pledge fees
	 */
	function getLastRecord() external view returns (uint256, uint256) {
		Record memory record = records[lastEpochReport];
		return (record.preCommitDeposit, record.initialPledge);
	}

	/**
	 * @notice Returns historical record PreCommitDeposit and Initial Pledge fees for specific `epoch`
	 * @param epoch Filecoin network Epoch number
	 */
	function getHistoricalRecord(uint256 epoch) external view returns (uint256, uint256) {
		Record memory record = records[epoch];
		return (record.preCommitDeposit, record.initialPledge);
	}

	/**
	 * @notice Returns last record for PreCommitDeposit fee
	 */
	function getLastPreCommitDeposit() external view returns (uint256) {
		return records[lastEpochReport].preCommitDeposit;
	}

	/**
	 * @notice Returns historical record for PreCommitDeposit fee with specified `epoch`
	 * @param epoch Filecoin network Epoch number
	 */
	function getHistoricalPreCommitDeposit(uint256 epoch) external view returns (uint256) {
		return records[epoch].preCommitDeposit;
	}

	/**
	 * @notice Returns last record for Initial Pledge fee
	 */
	function getLastInitialPledge() external view returns (uint256) {
		return records[lastEpochReport].initialPledge;
	}

	/**
	 * @notice Returns historical record for Initial Pledge fee with specified `epoch`
	 * @param epoch Filecoin network Epoch number
	 */
	function getHistoricalInitialPledge(uint256 epoch) external view returns (uint256) {
		return records[epoch].initialPledge;
	}
}

contracts/StorageProviderCollateral.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import {IStorageProviderCollateral} from "./interfaces/IStorageProviderCollateral.sol";
import {IStorageProviderRegistryClient} from "./interfaces/IStorageProviderRegistryClient.sol";
import {SafeTransferLib} from "./libraries/SafeTransferLib.sol";
import {StorageProviderTypes} from "./types/StorageProviderTypes.sol";
import {IWFIL} from "./libraries/tokens/IWFIL.sol";
import {ReentrancyGuard} from "lib/solmate/src/utils/ReentrancyGuard.sol";
import {FilAddress} from "lib/fevmate/contracts/utils/FilAddress.sol";
import {FixedPointMathLib} from "lib/solmate/src/utils/FixedPointMathLib.sol";
import {AccessControl} from "lib/openzeppelin-contracts/contracts//access/AccessControl.sol";

/**
 * @title Storage Provider Collateral stores collateral for covering potential
 * slashing risks by SPs (Storage Providers) in liquid staking protocol.
 *
 * The basis coverage is expected to be around 15% of the total FIL allocation
 * requested by SP. Over time as SPs earn FIL rewards, a locked portion of rewards
 * would be exchanged for the collateral provided upfront. Therefore locked SPs
 * collateral becomes accessible for withdrawals by stakers. This mechanism doesn't
 * create additional slashing risks as SPs are slashed by the locked rewards first,
 * making it a good option for collateralization in the system.
 *
 */
contract StorageProviderCollateral is IStorageProviderCollateral, AccessControl, ReentrancyGuard {
	using SafeTransferLib for address;
	using FixedPointMathLib for uint256;
	using FilAddress for address;

	// Mapping of storage provider collateral information to their owner ID
	mapping(uint64 => SPCollateral) public collaterals;

	// Mapping of storage provider total slashing amounts to their owner ID
	mapping(uint64 => uint256) public slashings;

	mapping(uint64 => uint256) public collateralRequirements;

	bytes32 private constant COLLATERAL_ADMIN = keccak256("COLLATERAL_ADMIN");

	uint256 public baseRequirements; // Number in basis points (10000 = 100%)
	uint256 public constant BASIS_POINTS = 10000;
	IStorageProviderRegistryClient public registry;

	IWFIL public immutable WFIL; // WFIL implementation

	// Storage Provider parameters
	struct SPCollateral {
		uint256 availableCollateral;
		uint256 lockedCollateral;
	}

	modifier activeStorageProvider(uint64 _ownerId) {
		require(registry.isActiveProvider(_ownerId), "INACTIVE_STORAGE_PROVIDER");
		_;
	}

	/**
	 * @dev Contract constructor function.
	 * @param _wFIL WFIL token implementation
	 *
	 */
	constructor(IWFIL _wFIL, address _registry, uint256 _baseRequirements) {
		WFIL = _wFIL;
		registry = IStorageProviderRegistryClient(_registry);

		require(_baseRequirements > 0 || _baseRequirements <= 10000, "BASE_REQUIREMENTS_OVERFLOW");
		baseRequirements = _baseRequirements;

		_setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
		grantRole(COLLATERAL_ADMIN, msg.sender);
	}

	receive() external payable virtual {}

	fallback() external payable virtual {}

	/**
	 * @dev Deposit `msg.value` FIL funds by the msg.sender into collateral
	 * @notice Wrapps of FIL into WFIL token internally
	 */
	function deposit() public payable nonReentrant {
		uint256 amount = msg.value;
		require(amount > 0, "INVALID_AMOUNT");

		address ownerAddr = msg.sender.normalize();
		(bool isID, uint64 ownerId) = ownerAddr.getActorID();
		require(isID, "INACTIVE_ACTOR_ID");
		require(registry.isActiveProvider(ownerId), "INACTIVE_STORAGE_PROVIDER");

		SPCollateral storage collateral = collaterals[ownerId];
		collateral.availableCollateral = collateral.availableCollateral + amount;

		_wrapFIL(address(this));

		emit StorageProviderCollateralDeposit(ownerId, amount);
	}

	/**
	 * @dev Withdraw `_amount` of FIL funds by the `msg.sender` from the collateral system
	 * @notice Unwraps of FIL into WFIL token internally and
	 * delivers maximum amount of FIL available for withdrawal if `_amount` is bigger.
	 */
	function withdraw(uint256 _amount) public nonReentrant {
		require(_amount > 0, "ZERO_AMOUNT");

		address ownerAddr = msg.sender.normalize();
		(bool isID, uint64 ownerId) = ownerAddr.getActorID();
		require(isID, "INACTIVE_ACTOR_ID");
		require(registry.isActiveProvider(ownerId), "INACTIVE_STORAGE_PROVIDER");

		(uint256 lockedWithdraw, uint256 availableWithdraw, bool isUnlock) = calcMaximumWithdraw(ownerId);
		uint256 maxWithdraw = lockedWithdraw + availableWithdraw;
		uint256 finalAmount = _amount > maxWithdraw ? maxWithdraw : _amount;
		uint256 delta;

		if (isUnlock) {
			delta = finalAmount - lockedWithdraw;
			collaterals[ownerId].lockedCollateral = collaterals[ownerId].lockedCollateral - lockedWithdraw; // 10 - 2 == 8
			collaterals[ownerId].availableCollateral = collaterals[ownerId].availableCollateral - delta; // 5 + 1 == 6

			_unwrapWFIL(msg.sender, finalAmount);
		} else {
			collaterals[ownerId].availableCollateral = collaterals[ownerId].availableCollateral - finalAmount;
		}

		emit StorageProviderCollateralWithdraw(ownerId, finalAmount);
	}

	/**
	 * @dev Locks required collateral amount based on `_allocated` FIL to pledge
	 * @notice Increases the total amount of locked collateral for storage provider
	 * @param _ownerId Storage provider owner ID
	 * @param _allocated FIL amount that is going to be pledged for Storage Provider
	 */
	function lock(uint64 _ownerId, uint256 _allocated) external activeStorageProvider(_ownerId) {
		require(registry.isActivePool(msg.sender), "INVALID_ACCESS");
		require(_allocated > 0, "ZERO_ALLOCATION");

		_rebalance(_ownerId, _allocated);
		registry.increaseUsedAllocation(_ownerId, _allocated, block.timestamp);
	}

	/**
	 * @dev Fits collateral amounts based on SP pledge usage, distributed rewards and pledge paybacks
	 * @notice Rebalances the total locked and available collateral amounts
	 * @param _ownerId Storage provider owner ID
	 */
	function fit(uint64 _ownerId) external activeStorageProvider(_ownerId) {
		require(registry.isActivePool(msg.sender), "INVALID_ACCESS");

		_rebalance(_ownerId, 0);
	}

	/**
	 * @dev Slashes SP for a `_slashingAmt` and delivers WFIL amount to the `msg.sender` LSP
	 * @notice Doesn't perform a rebalancing checks
	 * @param _ownerId Storage provider owner ID
	 * @param _slashingAmt Slashing amount for SP
	 */
	function slash(uint64 _ownerId, uint256 _slashingAmt) external activeStorageProvider(_ownerId) {
		require(registry.isActivePool(msg.sender), "INVALID_ACCESS");

		SPCollateral memory collateral = collaterals[_ownerId];
		if (_slashingAmt <= collateral.lockedCollateral) {
			collateral.lockedCollateral = collateral.lockedCollateral - _slashingAmt;
		} else {
			uint256 totalCollateral = collateral.lockedCollateral + collateral.availableCollateral;
			require(_slashingAmt <= totalCollateral, "NOT_ENOUGH_COLLATERAL"); // TODO: introduce debt for SP to cover worst case scenario
			uint256 delta = _slashingAmt - collateral.lockedCollateral;

			collateral.lockedCollateral = 0;
			collateral.availableCollateral = collateral.availableCollateral - delta;
		}

		collaterals[_ownerId] = collateral;
		slashings[_ownerId] += _slashingAmt;

		WFIL.transfer(msg.sender, _slashingAmt);

		emit StorageProviderCollateralSlash(_ownerId, _slashingAmt, msg.sender);
	}

	/**
	 * @notice Return Storage Provider Collateral information with `_provider` address
	 */
	function getCollateral(uint64 _ownerId) public view returns (uint256, uint256) {
		SPCollateral memory collateral = collaterals[_ownerId];
		return (collateral.availableCollateral, collateral.lockedCollateral);
	}

	/**
	 * @notice Return Storage Provider Available Collateral information with `_provider` address
	 */
	function getAvailableCollateral(uint64 _ownerId) public view returns (uint256) {
		return collaterals[_ownerId].availableCollateral;
	}

	/**
	 * @notice Return Storage Provider Locked Collateral information with `_provider` address
	 */
	function getLockedCollateral(uint64 _ownerId) public view returns (uint256) {
		return collaterals[_ownerId].lockedCollateral;
	}

	function getDebt(uint64 _ownerId) public view returns (uint256) {
		(, , uint256 usedAllocation, , , uint256 repaidPledge) = registry.allocations(_ownerId);

		uint256 _collateralRequirements = collateralRequirements[_ownerId];
		uint256 requirements = calcCollateralRequirements(usedAllocation, repaidPledge, 0, _collateralRequirements);
		SPCollateral memory collateral = collaterals[_ownerId];

		(uint256 adjAmt, bool isUnlock) = calcCollateralAdjustment(collateral.lockedCollateral, requirements);

		if (!isUnlock) {
			return adjAmt;
		}

		return 0;
	}

	/**
	 * @notice Calculates max collateral withdrawal amount for SP depending on the
	 * total used FIL allocation and locked rewards.
	 * @param _ownerId Storage Provider owner address
	 */
	function calcMaximumWithdraw(uint64 _ownerId) internal view returns (uint256, uint256, bool) {
		(, , uint256 usedAllocation, , , uint256 repaidPledge) = registry.allocations(_ownerId);

		uint256 _collateralRequirements = collateralRequirements[_ownerId];
		uint256 requirements = calcCollateralRequirements(usedAllocation, repaidPledge, 0, _collateralRequirements);
		SPCollateral memory collateral = collaterals[_ownerId];

		(uint256 adjAmt, bool isUnlock) = calcCollateralAdjustment(collateral.lockedCollateral, requirements);

		if (!isUnlock) {
			adjAmt = collateral.availableCollateral - adjAmt;

			return (0, adjAmt, isUnlock);
		} else {
			return (adjAmt, collateral.availableCollateral, isUnlock);
		}
	}

	/**
	 * @notice Rebalances collateral for a specified `_ownerId` with `_allocated` in mind
	 * @param _ownerId Storage Provider owner address
	 * @param _allocated Hypothetical allocation for SP
	 */
	function _rebalance(uint64 _ownerId, uint256 _allocated) internal {
		(uint256 allocationLimit, , uint256 usedAllocation, , , uint256 repaidPledge) = registry.allocations(_ownerId);

		if (_allocated > 0) {
			require(usedAllocation + _allocated <= allocationLimit, "ALLOCATION_OVERFLOW");
		}
		uint256 _collateralRequirements = collateralRequirements[_ownerId];
		uint256 totalRequirements = calcCollateralRequirements(
			usedAllocation,
			repaidPledge,
			_allocated,
			_collateralRequirements
		);

		SPCollateral memory collateral = collaterals[_ownerId];
		require(
			totalRequirements <= collateral.lockedCollateral + collateral.availableCollateral,
			"INSUFFICIENT_COLLATERAL"
		);

		(uint256 adjAmt, bool isUnlock) = calcCollateralAdjustment(collateral.lockedCollateral, totalRequirements);

		if (!isUnlock) {
			collateral.lockedCollateral = collateral.lockedCollateral + adjAmt;
			collateral.availableCollateral = collateral.availableCollateral - adjAmt;

			emit StorageProviderCollateralRebalance(_ownerId, adjAmt, 0, isUnlock);
		} else {
			collateral.lockedCollateral = collateral.lockedCollateral - adjAmt;
			collateral.availableCollateral = collateral.availableCollateral + adjAmt;

			emit StorageProviderCollateralRebalance(_ownerId, 0, adjAmt, isUnlock);
		}

		collaterals[_ownerId] = collateral;
	}

	/**
	 * @notice Calculates total collateral requirements for SP depending on the
	 * total used FIL allocation and locked rewards.
	 * @param _usedAllocation Already used FIL allocation by Storage Provider
	 * @param _repaidPledge Repaid pledge by SP
	 * @param _allocationToUse Allocation to be used by SP
	 * @param _collateralRequirements Percentage of collateral coverage
	 */
	function calcCollateralRequirements(
		uint256 _usedAllocation,
		uint256 _repaidPledge,
		uint256 _allocationToUse,
		uint256 _collateralRequirements
	) internal pure returns (uint256) {
		uint256 usedAllocation = _allocationToUse > 0 ? _usedAllocation + _allocationToUse : _usedAllocation;
		uint256 req = usedAllocation - _repaidPledge;

		if (req > 0) {
			return req.mulDivDown(_collateralRequirements, BASIS_POINTS);
		} else {
			return 0;
		}
	}

	/**
	 * @notice Calculates collateral adjustment for SP depending on the
	 * total locked collateral and overall collateral requirements.
	 * @param _lockedCollateral Locked collateral amount for Storage Provider
	 * @param _collateralRequirements Collateral requirements for SP
	 */
	function calcCollateralAdjustment(
		uint256 _lockedCollateral,
		uint256 _collateralRequirements
	) internal pure returns (uint256, bool) {
		if (_lockedCollateral > 0 && _collateralRequirements > 0) {
			if (_lockedCollateral > _collateralRequirements) {
				return (_lockedCollateral - _collateralRequirements, true);
			} else {
				return (_collateralRequirements - _lockedCollateral, false);
			}
		} else if (_lockedCollateral > 0 && _collateralRequirements == 0) {
			return (_lockedCollateral, true);
		} else if (_lockedCollateral == 0 && _collateralRequirements > 0) {
			return (_collateralRequirements, false);
		} else if (_lockedCollateral == 0 && _collateralRequirements == 0) {
			return (0, true);
		}
	}

	/**
	 * @dev Updates collateral requirements for SP with `_ownerId` by `requirements` percentage
	 * @notice Only triggered by Collateral admin or registry contract while registering SP
	 * @param _ownerId Storage provider owner ID
	 * @param requirements Percentage of collateral requirements
	 */
	function updateCollateralRequirements(uint64 _ownerId, uint256 requirements) external {
		require(hasRole(COLLATERAL_ADMIN, msg.sender) || msg.sender == address(registry), "INVALID_ACCESS");

		if (requirements == 0) {
			collateralRequirements[_ownerId] = baseRequirements;

			emit StorageProviderCollateralUpdate(_ownerId, 0, baseRequirements);
		} else {
			uint256 prevRequirements = collateralRequirements[_ownerId];
			require(requirements <= 10000, "COLLATERAL_REQUIREMENTS_OVERFLOW");
			require(requirements != prevRequirements, "SAME_COLLATERAL_REQUIREMENTS");

			collateralRequirements[_ownerId] = requirements;

			emit StorageProviderCollateralUpdate(_ownerId, prevRequirements, requirements);
		}
	}

	/**
	 * @notice Wraps FIL into WFIL and transfers it to the `_recipient` address
	 * @param _recipient WFIL recipient address
	 */
	function _wrapFIL(address _recipient) internal {
		uint256 amount = msg.value;

		WFIL.deposit{value: amount}();
		WFIL.transfer(_recipient, amount);
	}

	/**
	 * @notice Unwraps `_amount` of WFIL into FIL and transfers it to the `_recipient` address
	 * @param _recipient WFIL recipient address
	 */
	function _unwrapWFIL(address _recipient, uint256 _amount) internal {
		uint256 balanceWETH9 = WFIL.balanceOf(address(this));
		require(balanceWETH9 >= _amount, "Insufficient WETH9");

		if (balanceWETH9 > 0) {
			WFIL.withdraw(_amount);
			_recipient.safeTransferETH(_amount);
		}
	}

	/**
	 * @notice Updates base collateral requirements amount for Storage Providers
	 * @param requirements New base collateral requirements for SP
	 */
	function updateBaseCollateralRequirements(uint256 requirements) public {
		require(hasRole(COLLATERAL_ADMIN, msg.sender), "INVALID_ACCESS");
		require(requirements > 0, "INVALID_REQUIREMENTS");

		uint256 prevRequirements = baseRequirements;
		require(requirements != prevRequirements, "SAME_REQUIREMENTS");

		baseRequirements = requirements;

		emit UpdateBaseCollateralRequirements(requirements);
	}

	/**
	 * @notice Updates StorageProviderRegistry contract address
	 * @param newAddr StorageProviderRegistry contract address
	 */
	function setRegistryAddress(address newAddr) public {
		require(hasRole(COLLATERAL_ADMIN, msg.sender), "INVALID_ACCESS");
		require(newAddr != address(0), "INVALID_ADDRESS");

		address prevRegistry = address(registry);
		require(prevRegistry != newAddr, "SAME_ADDRESS");

		registry = IStorageProviderRegistryClient(newAddr);

		emit SetRegistryAddress(newAddr);
	}
}

contracts/StorageProviderRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import {MinerAPI, MinerTypes, CommonTypes} from "lib/filecoin-solidity/contracts/v0.8/MinerAPI.sol";
import {PrecompilesAPI} from "lib/filecoin-solidity/contracts/v0.8/PrecompilesAPI.sol";
import {StorageProviderTypes} from "./types/StorageProviderTypes.sol";
import {FilAddress} from "lib/fevmate/contracts/utils/FilAddress.sol";
import {ReentrancyGuard} from "lib/solmate/src/utils/ReentrancyGuard.sol";

import {AccessControl} from "lib/openzeppelin-contracts/contracts//access/AccessControl.sol";
import {Counters} from "lib/openzeppelin-contracts/contracts//utils/Counters.sol";
import {BokkyPooBahsDateTimeLibrary} from "./libraries/DateTimeLibraryCompressed.sol";
import {IStorageProviderRegistry} from "./interfaces/IStorageProviderRegistry.sol";
import {ILiquidStakingClient} from "./interfaces/ILiquidStakingClient.sol";
import {IStorageProviderCollateralClient} from "./interfaces/IStorageProviderCollateralClient.sol";

/**
 * @title Storage Provider Registry contract allows storage providers to register
 * in liquid staking protocol and ask for a FIL allocation.
 *
 * Once Storage Provider is registered and signaled their desired FIL allocation
 * it needs to transfer
 *
 */
contract StorageProviderRegistry is IStorageProviderRegistry, AccessControl, ReentrancyGuard {
	using Counters for Counters.Counter;
	using FilAddress for address;

	// Mapping of storage provider IDs to their storage provider info
	mapping(uint64 => StorageProviderTypes.StorageProvider) public storageProviders;

	// Mapping of storage provider IDs to their restaking info
	mapping(uint64 => StorageProviderTypes.SPAllocation) public allocations;

	// Mapping of storage provider IDs to their restaking info
	mapping(uint64 => StorageProviderTypes.SPRestaking) public restakings;

	// Mapping of storage provider IDs to their allocation update requests
	mapping(uint64 => StorageProviderTypes.AllocationRequest) public allocationRequests;

	// Mapping of storage provider IDs to their sector sizes
	mapping(uint64 => uint64) public sectorSizes;

	// Mapping of storage providers daily allocation usage to date hashes
	mapping(bytes32 => uint256) public dailyUsages;

	mapping(address => bool) public pools;

	Counters.Counter public totalStorageProviders;
	Counters.Counter public totalInactiveStorageProviders;

	bytes32 private constant REGISTRY_ADMIN = keccak256("REGISTRY_ADMIN");

	uint256 public maxAllocation;

	IStorageProviderCollateralClient public collateral;

	modifier activeStorageProvider(uint64 _ownerId) {
		require(storageProviders[_ownerId].active, "INACTIVE_STORAGE_PROVIDER");
		_;
	}

	/**
	 * @dev Contract constructor function.
	 * @param _maxAllocation Number of maximum FIL allocated to a single storage provider
	 */
	constructor(uint256 _maxAllocation) {
		_setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
		grantRole(REGISTRY_ADMIN, msg.sender);
		maxAllocation = _maxAllocation;
	}

	struct RegisterLocalVars {
		address ownerAddr;
		bool isID;
		uint64 msgSenderId;
		uint64 ownerId;
		uint64 sectorSize;
	}

	/**
	 * @notice Register storage provider with `_minerId`, desired `_allocationLimit` and `_targetPool`
	 * @param _minerId Storage Provider miner ID in Filecoin network
	 * @param _targetPool Target liquid staking strategy
	 * @param _allocationLimit FIL allocation for storage provider
	 * @param _dailyAllocation Daily FIL allocation for storage provider
	 * @dev Only triggered by Storage Provider owner
	 */
	function register(
		uint64 _minerId,
		address _targetPool,
		uint256 _allocationLimit,
		uint256 _dailyAllocation
	) public virtual override nonReentrant {
		require(_allocationLimit <= maxAllocation, "INVALID_ALLOCATION");
		require(pools[_targetPool], "INVALID_TARGET_POOL");

		RegisterLocalVars memory vars;

		vars.ownerAddr = msg.sender.normalize();
		(vars.isID, vars.msgSenderId) = vars.ownerAddr.getActorID();
		require(vars.isID, "INACTIVE_ACTOR_ID");

		CommonTypes.FilActorId actorId = CommonTypes.FilActorId.wrap(_minerId);

		MinerTypes.GetOwnerReturn memory ownerReturn = MinerAPI.getOwner(actorId);
		require(keccak256(ownerReturn.proposed.data) == keccak256(bytes("")), "PROPOSED_NEW_OWNER");

		vars.ownerId = PrecompilesAPI.resolveAddress(ownerReturn.owner);
		require(vars.ownerId == vars.msgSenderId, "INVALID_MINER_OWNERSHIP");
		require(!storageProviders[vars.ownerId].onboarded, "ALREADY_REGISTERED");

		StorageProviderTypes.StorageProvider storage storageProvider = storageProviders[vars.ownerId];
		storageProvider.minerId = _minerId;
		storageProvider.targetPool = _targetPool;

		StorageProviderTypes.SPAllocation storage spAllocation = allocations[vars.ownerId];
		spAllocation.allocationLimit = _allocationLimit;
		spAllocation.dailyAllocation = _dailyAllocation;

		vars.sectorSize = MinerAPI.getSectorSize(actorId);
		sectorSizes[vars.ownerId] = vars.sectorSize;

		totalStorageProviders.increment();
		totalInactiveStorageProviders.increment();

		collateral.updateCollateralRequirements(vars.ownerId, 0);
		ILiquidStakingClient(_targetPool).updateProfitShare(vars.ownerId, 0);

		emit StorageProviderRegistered(
			ownerReturn.owner.data,
			vars.ownerId,
			_minerId,
			_targetPool,
			_allocationLimit,
			_dailyAllocation
		);
	}

	/**
	 * @notice Onboard storage provider with `_minerId`, desired `_allocationLimit`, `_repayment` amount
	 * @param _minerId Storage Provider miner ID in Filecoin network
	 * @param _allocationLimit FIL allocation for storage provider
	 * @param _dailyAllocation Daily FIL allocation for storage provider
	 * @param _repayment FIL repayment for storage provider
	 * @param _lastEpoch Last epoch for FIL allocation utilization
	 * @dev Only triggered by registry admin
	 */
	function onboardStorageProvider(
		uint64 _minerId,
		uint256 _allocationLimit,
		uint256 _dailyAllocation,
		uint256 _repayment,
		int64 _lastEpoch
	) public virtual nonReentrant {
		require(hasRole(REGISTRY_ADMIN, msg.sender), "INVALID_ACCESS");
		require(_repayment > _allocationLimit, "INCORRECT_REPAYMENT");
		require(_allocationLimit <= maxAllocation, "INCORRECT_ALLOCATION");
		CommonTypes.FilActorId actorId = CommonTypes.FilActorId.wrap(_minerId);

		MinerTypes.GetOwnerReturn memory ownerReturn = MinerAPI.getOwner(actorId);
		require(keccak256(bytes("")) == keccak256(ownerReturn.proposed.data), "PROPOSED_NEW_OWNER");

		uint64 ownerId = PrecompilesAPI.resolveAddress(ownerReturn.owner);

		StorageProviderTypes.StorageProvider storage storageProvider = storageProviders[ownerId];
		StorageProviderTypes.SPAllocation storage spAllocation = allocations[ownerId];
		require(!storageProviders[ownerId].onboarded, "ALREADY_REGISTERED");

		storageProvider.onboarded = true;
		storageProvider.lastEpoch = _lastEpoch;

		spAllocation.repayment = _repayment;
		spAllocation.allocationLimit = _allocationLimit;
		spAllocation.dailyAllocation = _dailyAllocation;

		emit StorageProviderOnboarded(ownerId, _minerId, _allocationLimit, _dailyAllocation, _repayment, _lastEpoch);
	}

	/**
	 * @notice Transfer beneficiary address of a miner to the target pool
	 */
	function changeBeneficiaryAddress() public virtual override nonReentrant {
		address ownerAddr = msg.sender.normalize();
		(bool isID, uint64 ownerId) = ownerAddr.getActorID();
		require(isID, "INACTIVE_ACTOR_ID");

		StorageProviderTypes.StorageProvider memory storageProvider = storageProviders[ownerId];
		require(storageProvider.onboarded, "NON_ONBOARDED_SP");

		ILiquidStakingClient(storageProviders[ownerId].targetPool).forwardChangeBeneficiary(
			storageProvider.minerId,
			storageProvider.targetPool,
			allocations[ownerId].repayment,
			storageProvider.lastEpoch
		);

		emit StorageProviderBeneficiaryAddressUpdated(storageProvider.targetPool);
	}

	/**
	 * @notice Accept beneficiary address transfer and activate FIL allocation
	 * @param _ownerId Storage Provider owner ID
	 * @dev Only triggered by registry admin
	 */
	function acceptBeneficiaryAddress(uint64 _ownerId) public virtual override nonReentrant {
		require(hasRole(REGISTRY_ADMIN, msg.sender), "INVALID_ACCESS");

		StorageProviderTypes.StorageProvider memory storageProvider = storageProviders[_ownerId];
		require(storageProvider.onboarded, "NON_ONBOARDED_SP");

		ILiquidStakingClient(storageProviders[_ownerId].targetPool).forwardChangeBeneficiary(
			storageProvider.minerId,
			storageProvider.targetPool,
			allocations[_ownerId].repayment,
			storageProvider.lastEpoch
		);

		storageProviders[_ownerId].active = true;
		totalInactiveStorageProviders.decrement();

		emit StorageProviderBeneficiaryAddressAccepted(_ownerId);
	}

	/**
	 * @notice Deactive storage provider with ID `_ownerId`
	 * @param _ownerId Storage Provider owner ID
	 * @dev Only triggered by registry admin
	 */
	function deactivateStorageProvider(uint64 _ownerId) public activeStorageProvider(_ownerId) {
		require(hasRole(REGISTRY_ADMIN, msg.sender), "INVALID_ACCESS");
		storageProviders[_ownerId].active = false;
		totalInactiveStorageProviders.increment();

		emit StorageProviderDeactivated(_ownerId);
	}

	/**
	 * @notice Update storage provider miner ID with `_minerId`
	 * @param _ownerId Storage Provider owner ID
	 * @param _minerId Storage Provider new miner ID
	 * @dev Only triggered by registry admin
	 */
	function setMinerAddress(uint64 _ownerId, uint64 _minerId) public virtual activeStorageProvider(_ownerId) {
		require(hasRole(REGISTRY_ADMIN, msg.sender), "INVALID_ACCESS");
		uint64 prevMiner = storageProviders[_ownerId].minerId;
		require(prevMiner != _minerId, "SAME_MINER");

		CommonTypes.FilActorId actorId = CommonTypes.FilActorId.wrap(_minerId);

		MinerTypes.GetOwnerReturn memory ownerReturn = MinerAPI.getOwner(actorId);
		require(keccak256(ownerReturn.proposed.data) == keccak256(bytes("")), "PROPOSED_NEW_OWNER");

		uint64 ownerId = PrecompilesAPI.resolveAddress(ownerReturn.owner);
		require(ownerId == _ownerId, "INVALID_MINER_OWNERSHIP");

		storageProviders[_ownerId].minerId = _minerId;

		emit StorageProviderMinerAddressUpdate(_ownerId, _minerId);
	}

	/**
	 * @notice Request storage provider's FIL allocation update with `_allocationLimit`
	 * @param _allocationLimit New FIL allocation for storage provider
	 * @param _dailyAllocation New daily FIL allocation for storage provider
	 * @dev Only triggered by Storage Provider owner
	 */
	function requestAllocationLimitUpdate(uint256 _allocationLimit, uint256 _dailyAllocation) public virtual override {
		address ownerAddr = msg.sender.normalize();
		(bool isID, uint64 ownerId) = ownerAddr.getActorID();
		require(isID, "INACTIVE_ACTOR_ID");

		StorageProviderTypes.StorageProvider memory storageProvider = storageProviders[ownerId];
		require(storageProvider.active, "INACTIVE_STORAGE_PROVIDER");

		StorageProviderTypes.SPAllocation memory spAllocation = allocations[ownerId];
		require(
			spAllocation.allocationLimit != _allocationLimit || spAllocation.dailyAllocation != _dailyAllocation,
			"SAME_ALLOCATION_LIMIT"
		);
		require(_allocationLimit <= maxAllocation, "ALLOCATION_OVERFLOW");

		StorageProviderTypes.AllocationRequest storage allocationRequest = allocationRequests[ownerId];
		allocationRequest.allocationLimit = _allocationLimit;
		allocationRequest.dailyAllocation = _dailyAllocation;

		emit StorageProviderAllocationLimitRequest(ownerId, _allocationLimit, _dailyAllocation);
	}

	/**
	 * @notice Update storage provider FIL allocation with `_allocationLimit`
	 * @param _ownerId Storage provider owner ID
	 * @param _allocationLimit New FIL allocation for storage provider
	 * @param _dailyAllocation New daily FIL allocation for storage provider
	 * @param _repaymentAmount New FIL repayment amount for storage provider
	 * @dev Only triggered by registry admin
	 */
	function updateAllocationLimit(
		uint64 _ownerId,
		uint256 _allocationLimit,
		uint256 _dailyAllocation,
		uint256 _repaymentAmount
	) public virtual override activeStorageProvider(_ownerId) nonReentrant {
		require(hasRole(REGISTRY_ADMIN, msg.sender), "INVALID_ACCESS"); // 0xFf0000000000000000009bd -> ID: 2455 -> t02455

		StorageProviderTypes.AllocationRequest memory allocationRequest = allocationRequests[_ownerId];
		require(allocationRequest.allocationLimit == _allocationLimit, "INVALID_ALLOCATION");
		require(allocationRequest.dailyAllocation == _dailyAllocation, "INVALID_DAILY_ALLOCATION");

		StorageProviderTypes.StorageProvider memory storageProvider = storageProviders[_ownerId];

		ILiquidStakingClient(storageProviders[_ownerId].targetPool).forwardChangeBeneficiary(
			storageProvider.minerId,
			storageProvider.targetPool,
			_repaymentAmount,
			storageProvider.lastEpoch
		);

		StorageProviderTypes.SPAllocation storage spAllocation = allocations[_ownerId];
		spAllocation.allocationLimit = _allocationLimit;
		spAllocation.dailyAllocation = _dailyAllocation;
		spAllocation.repayment = _repaymentAmount;

		delete allocationRequests[_ownerId];

		emit StorageProviderAllocationLimitUpdate(_ownerId, _allocationLimit, _dailyAllocation, _repaymentAmount);
	}

	/**
	 * @notice Update storage provider's restaking ratio
	 * @param _restakingRatio Restaking ratio for Storage Provider
	 * @param _restakingAddress Restaking address (f4 address) for Storage Provider
	 * @dev Only triggered by Storage Provider
	 */
	function setRestaking(uint256 _restakingRatio, address _restakingAddress) public virtual override {
		uint64 ownerId = PrecompilesAPI.resolveEthAddress(msg.sender);

		require(_restakingRatio <= 10000, "INVALID_RESTAKING_RATIO");
		require(_restakingAddress != address(0), "INVALID_ADDRESS");

		StorageProviderTypes.SPRestaking storage restaking = restakings[ownerId];
		restaking.restakingRatio = _restakingRatio;
		restaking.restakingAddress = _restakingAddress;

		emit StorageProviderMinerRestakingRatioUpdate(ownerId, _restakingRatio, _restakingAddress);
	}

	/**
	 * @notice Return total number of storage providers in liquid staking
	 */
	function getTotalStorageProviders() public view returns (uint256) {
		return totalStorageProviders.current();
	}

	/**
	 * @notice Return total number of currently active storage providers
	 */
	function getTotalActiveStorageProviders() public view returns (uint256) {
		return totalStorageProviders.current() - totalInactiveStorageProviders.current();
	}

	/**
	 * @notice Return Storage Provider information with `_ownerId`
	 */
	function getStorageProvider(uint64 _ownerId) public view returns (bool, address, uint64, int64) {
		StorageProviderTypes.StorageProvider memory storageProvider = storageProviders[_ownerId];
		return (storageProvider.active, storageProvider.targetPool, storageProvider.minerId, storageProvider.lastEpoch);
	}

	/**
	 * @notice Return a boolean flag of Storage Provider activity
	 */
	function isActiveProvider(uint64 _ownerId) external view returns (bool status) {
		status = storageProviders[_ownerId].active;
	}

	/**
	 * @notice Increase collected rewards by Storage Provider
	 * @param _ownerId Storage Provider owner ID
	 * @param _accuredRewards Withdrawn rewards from SP's miner actor
	 */
	function increaseRewards(uint64 _ownerId, uint256 _accuredRewards) external {
		require(pools[msg.sender], "INVALID_ACCESS");

		StorageProviderTypes.SPAllocation storage spAllocation = allocations[_ownerId];
		spAllocation.accruedRewards = spAllocation.accruedRewards + _accuredRewards;

		emit StorageProviderAccruedRewards(_ownerId, _accuredRewards);
	}

	/**
	 * @notice Increase repaid pledge by Storage Provider
	 * @param _ownerId Storage Provider owner ID
	 * @param _repaidPledge Withdrawn initial pledge after sector termination
	 */
	function increasePledgeRepayment(uint64 _ownerId, uint256 _repaidPledge) external {
		require(pools[msg.sender], "INVALID_ACCESS");

		StorageProviderTypes.SPAllocation storage spAllocation = allocations[_ownerId];
		spAllocation.repaidPledge = spAllocation.repaidPledge + _repaidPledge;
		require(spAllocation.repaidPledge <= spAllocation.usedAllocation, "PLEDGE_REPAYMENT_OVERFLOW");

		emit StorageProviderRepaidPledge(_ownerId, _repaidPledge);
	}

	/**
	 * @notice Increase used allocation for Storage Provider
	 * @param _ownerId Storage Provider owner ID
	 * @param _allocated FIL amount that is going to be pledged for Storage Provider
	 * @param _timestamp Transaction timestamp
	 */
	function increaseUsedAllocation(uint64 _ownerId, uint256 _allocated, uint256 _timestamp) external {
		require(msg.sender == address(collateral), "INVALID_ACCESS");

		(uint year, uint month, uint day) = BokkyPooBahsDateTimeLibrary.timestampToDate(_timestamp);
		bytes32 dateHash = keccak256(abi.encodePacked(year, month, day));

		uint256 usedDailyAlloc = dailyUsages[dateHash];
		uint256 totalDailyUsage = usedDailyAlloc + _allocated;

		StorageProviderTypes.SPAllocation storage spAllocation = allocations[_ownerId];

		require(totalDailyUsage <= spAllocation.dailyAllocation, "DAILY_ALLOCATION_OVERFLOW");
		// require(spAllocation.usedAllocation + _allocated <= spAllocation.allocationLimit, "TOTAL_ALLOCATION_OVERFLOW");

		spAllocation.usedAllocation = spAllocation.usedAllocation + _allocated;
		dailyUsages[dateHash] += _allocated;

		emit StorageProviderAllocationUsed(_ownerId, _allocated);
	}

	/**
	 * @notice Update StorageProviderCollateral smart contract
	 * @param _collateral StorageProviderCollateral smart contract address
	 * @dev Only triggered by registry admin
	 */
	function setCollateralAddress(address _collateral) public {
		require(hasRole(REGISTRY_ADMIN, msg.sender), "INVALID_ACCESS");
		require(_collateral != address(0), "INVALID_ADDRESS");

		address prevCollateral = address(collateral);
		require(prevCollateral != _collateral, "SAME_ADDRESS");

		collateral = IStorageProviderCollateralClient(_collateral);

		emit CollateralAddressUpdated(_collateral);
	}

	/**
	 * @notice Register new liquid staking pool
	 * @param _pool Address of pool smart contract
	 * @dev Only triggered by registry admin
	 */
	function registerPool(address _pool) public {
		require(hasRole(REGISTRY_ADMIN, msg.sender), "INVALID_ACCESS");
		require(_pool != address(0), "INVALID_ADDRESS");
		require(!pools[_pool], "ALREADY_ACTIVE_POOL");

		pools[_pool] = true;

		emit LiquidStakingPoolRegistered(_pool);
	}

	/**
	 * @notice Updates maximum allocation amount for SP
	 * @param allocation New max allocation per SP
	 */
	function updateMaxAllocation(uint256 allocation) public {
		require(hasRole(REGISTRY_ADMIN, msg.sender), "INVALID_ACCESS");
		require(allocation > 0, "INVALID_ALLOCATION");

		uint256 prevAllocation = maxAllocation;
		require(allocation != prevAllocation, "SAME_ALLOCATION");

		maxAllocation = allocation;

		emit UpdateMaxAllocation(allocation);
	}

	/**
	 * @notice Return a boolean flag whether `_pool` is active or not
	 */
	function isActivePool(address _pool) external view returns (bool) {
		return pools[_pool];
	}
}

